} impl<C: TNetworkClient> Service<C> { fn do_stuff(&self) -> i32 { self.client.fetch_id_matching(3) }
} #[cfg(test)]
struct MockNetworkClient { mocked_fetch_id_matching_result: i32, mocked_fetch_id_matching_argument: std::cell::Cell<u32>,
} #[cfg(test)]
impl TNetworkClient for MockNetworkClient { fn fetch_id_matching(&self, a: u32) -> i32 { self.mocked_fetch_id_matching_argument.set(a); self.mocked_fetch_id_matching_result }
} # struct MockNetworkClient {
